//** Get CIN, NPI and TIN from headers for CCDA FHIR Bundle - Required for FHIR Bundle conversion only**//
if (requestedPath == "/ccda/Bundle/" || requestedPath == "/ccda/Bundle") {
	//1.CIN
	checkRequiredHeader('X-TechBD-CIN', 'X-TechBD-CIN', true, 'patientCIN');

	//2.NPI
	var organizationNPI = $('headers').getHeader('X-TechBD-OrgNPI');
	logger.info("X-TechBD-OrgNPI: " + organizationNPI);

	//3.TIN
	var organizationTIN = $('headers').getHeader('X-TechBD-OrgTIN');
	logger.info("X-TechBD-OrgTIN: " + organizationTIN);

	// Check if both are missing — only then it's an error
	if ((organizationNPI == null || String(organizationNPI).trim() === "") &&
	    (organizationTIN == null || String(organizationTIN).trim() === "")) {
	    missingHeaders.push("Missing required header X-TechBD-OrgNPI and X-TechBD-OrgTIN. One is mandatory.");
	} else {
	    if (organizationNPI && String(organizationNPI).trim() !== "") {
	        channelMap.put('organizationNPI', organizationNPI);
	    }
	    if (organizationTIN && String(organizationTIN).trim() !== "") {
	        channelMap.put('organizationTIN', organizationTIN);
	    }
	}

	//4. Facility Code
	checkRequiredHeader('X-TechBD-Facility-ID', 'X-TechBD-Facility-ID', true, 'facilityID');

	//5. Encounter Type
	checkRequiredHeader('X-TechBD-Encounter-Type', 'X-TechBD-Encounter-Type', true, 'encounterType');

	//Screening code (used for Grouper resource) for Epic CCD files
	var screeningCode = $('headers').getHeader('X-TechBD-Screening-Code');	
	logger.info("X-TechBD-Screening-Code: " + screeningCode);
	if (screeningCode && String(screeningCode).trim() !== "") {
	    channelMap.put('X-TechBD-Screening-Code', screeningCode);
	}
}

// If any missing headers were found, throw a single error
if (missingHeaders.length > 0) {
    var errorMessage = "Bad Request: " + missingHeaders.join("; ");
    logger.error(errorMessage);
    setErrorResponse(400, errorMessage);
    throw errorMessage;
}

