/*
* Util function to get vender identifier details and choose the xslt file according to the vendor
*/
function getManufacturerModelName(xmlData) {
	try {
		if (xmlData) {
			var ccdaSchemaFolder = channelMap.get('ccdaSchemaFolder');
			var xmlDoc = new XML(xmlData); // Convert XML string to XML object
			var cdaPhiFilterXsltPath = ccdaSchemaFolder + "/cda-phi-filter.xslt";
			var cdaFhirBundleXsltPath = ccdaSchemaFolder + "/cda-fhir-bundle.xslt";

			var manufacturerModelName = '';
			var ehrVendor = '';
			var lowerName = '';
			var modelNode = '';

			manufacturerModelName = xmlDoc.*::author.*::assignedAuthor.*::assignedAuthoringDevice.*::manufacturerModelName.toString();
			if (manufacturerModelName != undefined && manufacturerModelName.toString().length > 0) {
				logger.info("Extracted Manufacturer Model Name: " + manufacturerModelName);
				lowerName = manufacturerModelName.toLowerCase();
			} else {
			     logger.warn("manufacturerModelName is empty");
			}

			if (lowerName.indexOf("epic") !== -1) {
			    	ehrVendor = "Epic";
			    	cdaPhiFilterXsltPath = ccdaSchemaFolder + "/cda-phi-filter-epic.xslt";
				cdaFhirBundleXsltPath = ccdaSchemaFolder + "/cda-fhir-bundle-epic.xslt";
			} else if (lowerName.indexOf("medent") !== -1) {
			    	ehrVendor = "Medent";
			    	cdaPhiFilterXsltPath = ccdaSchemaFolder + "/cda-phi-filter-medent.xslt";
				cdaFhirBundleXsltPath = ccdaSchemaFolder + "/cda-fhir-bundle-medent.xslt";
			} else {
			    	ehrVendor = "Other";
			    	cdaPhiFilterXsltPath = ccdaSchemaFolder + "/cda-phi-filter.xslt";
				cdaFhirBundleXsltPath = ccdaSchemaFolder + "/cda-fhir-bundle.xslt";
			}

			logger.info("ehrVendor: " + ehrVendor);
			channelMap.put('ehrVendor', ehrVendor);
			channelMap.put('cdaPhiFilter', cdaPhiFilterXsltPath);
			channelMap.put('cdaPhiBundle', cdaFhirBundleXsltPath);

			logger.info("using cdaPhiFilter: " + channelMap.get('cdaPhiFilter'));
			logger.info("using cdaPhiBundle: " + channelMap.get('cdaPhiBundle'));

		} else {
		    logger.error("xmlData is undefined or empty.");
		}
	} catch (e) {
        //logger.warn("Error parsing manufacturerModelName: " + e);
        var errorMsg;
        if (e instanceof JavaException) {
           errorMsg = "Error parsing manufacturerModelName: " + e.toString();
        } else {
           errorMsg = "Unexpected error during validation: " + e.message;
        }
        logger.error(errorMsg);

        // Failure: Return an OperationOutcome JSON response with validation errors
        responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "invalid")));

        throw new Error(errorMsg);
     }
}

/**
* Util function to generate a hash string using sha-256 that can be used as the resource id in FHIR Bundle.
*/
function generateSHA256(inputString) {
    var md = Packages.java.security.MessageDigest.getInstance("SHA-256");
    md.update(inputString.getBytes("UTF-8"));   
    var digest = md.digest();
    return bytesToHex(digest);
}

function bytesToHex(byteArray) {
    var hexString = new java.lang.StringBuilder();
    for (var i = 0; i < byteArray.length; i++) {
        var hex = java.lang.Integer.toHexString(0xff & byteArray[i]);
        if (hex.length == 1) hexString.append('0');
        hexString.append(hex);
    }
    return hexString.toString();
}

function getPatientMRN(patientRoles) {
    var extensionAttr = '';
    var ehrVendor = channelMap.get('ehrVendor');

    function isSSNFormat(ext) {
    	    return (
	        String.fromCharCode(ext.charCodeAt(3)) == '-' &&
	        String.fromCharCode(ext.charCodeAt(6)) == '-' &&
	        /^\d{3}-\d{2}-\d{4}$/.test(ext)
	    );
    }

	if (patientRoles != undefined) {
        var patientRole = patientRoles;
        var idNodes = patientRole.*::id;

        // Step 1: Check for Medent MRN
        if (ehrVendor == 'Medent') {
             for each (var idNode in idNodes) {
                var root = idNode.@root.toString();
                var ext = idNode.@extension.toString();

                if (root !== "2.16.840.1.113883.4.1" && !isSSNFormat(ext)) {
                    extensionAttr = ext;
                    logger.info("Patient MRN (Medent): " + extensionAttr);
                    break;
                }
            }
        }
	   else {	
        // Step 2: If Epic not found, fallback to Epic logic
            for each (var idNode in idNodes) {
                var root = idNode.@root.toString();
                var ext = idNode.@extension.toString();
                var assigningAuthorityName = idNode.@assigningAuthorityName.toString();

                if (assigningAuthorityName === "EPI" &&
                    (root === "1.2.840.114350.1.13.570.2.7.5.737384.14" || root !== "1.2.840.114350.1.1") &&
                    !isSSNFormat(ext)
                ) {
                    extensionAttr = ext;
                    logger.info("Patient MRN (Epic): " + extensionAttr);
                    break;
                }
            }
	   }        
    } else {
        logger.error("PatientRole not found in the XML.");
    }

    if (!extensionAttr) {
       logger.warn("No matching MRN found.");
    }

    channelMap.put('Patient-MRN', extensionAttr);
}

function getEncounterEffectiveTime(encounterNodeList) {
    var fallbackTime = new java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss.SS'Z'").format(new java.util.Date());
    var effectiveTimeOut = '';

    if (!encounterNodeList || encounterNodeList.length === 0) {	
        return fallbackTime;
    }

    var encounterNode = encounterNodeList[0];

    // Try to get low/@value
    var lowValue = encounterNode.*::effectiveTime.*::low.@value.toString();
    if (lowValue && lowValue.length > 0) {
        logger.info("effectiveTime (low): " + lowValue);
        return lowValue;
    }

    // Try to get effectiveTime/@value
    var directValue = encounterNode.*::effectiveTime.@value.toString();
    if (directValue && directValue.length > 0) {
        logger.info("effectiveTime (direct): " + directValue);
        return directValue;
    }

    return fallbackTime;
}

function generateSHA256Id(resource, resourceName, resourceIdName, transformer, additionalString) {
    if (resource != undefined) { 
        // Get MRN and CIN from channelMap
        var mrn = channelMap.get('Patient-MRN') || "";
        var cin = channelMap.get('patientCIN') || "";
        var combinedText = mrn + cin;

        if (typeof additionalString !== 'undefined' && additionalString != null && additionalString !== '') {
            combinedText += additionalString;
            logger.info("Including additionalString in SHA-256: " + additionalString);
        }

        var resourceText = resourceName + resource.toString().trim();
        combinedText += resourceText;

        // Generate SHA-256 hash
        var sha256Hash = generateSHA256(new java.lang.String(combinedText));
        transformer.setParameter(resourceIdName, sha256Hash);
        logger.info(resourceIdName + " : " + sha256Hash);
    } else {
        logger.error(resourceName + " not found in the XML.");
    }

    return transformer;
}

function getCategoryDisplay(code) {
    switch (code) {
        case '71802-3':
            return 'Housing Instability';
        case '96778-6':
            return 'Inadequate Housing';
        case '96779-4':
            return 'Utility Insecurity';
        case '88122-7':
        case '88123-5':
            return 'Food Insecurity';
        case '93030-5':
            return 'Transportation Insecurity';
        case '96780-2':
            return 'Employment Status';
        case '96782-8':
        case '95618-5':
        case '95617-7':
        case '95616-9':
        case '95615-1':
        case '95614-4':
            return 'SDOH Category Unspecified';
        default:
            return 'SDOH Category Unspecified';
    }
}

function getCategoryCode(code) {
    switch (code) {
        case '71802-3':
            return 'housing-instability';
        case '96778-6':
            return 'inadequate-housing';
        case '96779-4':
            return 'utility-insecurity';
        case '88122-7':
        case '88123-5':
            return 'food-insecurity';
        case '93030-5':
            return 'transportation-insecurity';
        case '96780-2':
            return 'employment-status';
        case '96782-8':
        case '95618-5':
        case '95617-7':
        case '95616-9':
        case '95615-1':
        case '95614-4':
            return 'sdoh-category-unspecified';
        default:
            return null;
    }
}

// Define Namespace Resolver
function getObservationCategoryCodes(transformer, observations) {    
    var xml = new XML(observations); // Parse the source message as XML    
    var ccda = new Namespace("urn:hl7-org:v3"); // Define the CCDA namespace    
    default xml namespace = ccda; // Set default namespace so ccda tags can be used without prefix

    // Extract all observation code attributes
    if (channelMap.get('ehrVendor') == 'Medent') {
	    var codes = xml..component
	                   .structuredBody
	                   .component
	                   .section.(@ID == 'observations')
	                   .entry
	                   .observation
	                   .entryRelationship
	                   .observation
	                   .code.@code;
     }
     else {
     	var codes = xml..component
	                   .structuredBody
	                   .component
	                   .section.(@ID == 'observations')
	                   .entry
	                   .observation
	                   .entryRelationship
	                   .observation
	                   .entryRelationship
	                   .observation
	                   .code.@code;
     }

	// collect unique category codes
	var categorySet = {};
	for (var i = 0; i < codes.length(); i++) {
	    var code = getCategoryCode(codes[i].toString().trim());
	    if (code && !(code in categorySet)) {
	        categorySet[code] = getCategoryDisplay(codes[i].toString().trim());  
	    }
	}

	var categoryJsonArray = [];
	for (var code in categorySet) {
	    categoryJsonArray.push({
	        code: code,
	        display: categorySet[code]
	    });
	}

	//Build Category json from Category codes to pass to the xslt file as transformer parameter
	var categoryXml = '[';
	for (var i = 0; i < categoryJsonArray.length; i++) {
	    categoryXml += '{' +
	        '"system": "http://hl7.org/fhir/us/sdoh-clinicalcare/CodeSystem/SDOHCC-CodeSystemTemporaryCodes",' +
	        '"code": "' + categoryJsonArray[i].code + '",' +
	        '"display": "' + categoryJsonArray[i].display + '"}';

	    if (i !== categoryJsonArray.length - 1) {
	        categoryXml += ',';
	    }
	}	
	categoryXml += ']';              

	// set context variable
	//msg['categoryXml'] = categoryXml;
	transformer.setParameter('categoryXml', categoryXml);
	logger.info("categoryXml : " + categoryXml);
	return transformer;
}

function getMultipleAnswersForComponent(transformer, observations) {
	if (observations == null) {
        return transformer;
    }

    //var xml = new XML(observations); // Parse the source message as XML    
    var xml;
    try {
        xml = new XML(observations.toString()); // Safely parse the XML
    } catch (e) {
        logger.error("Error parsing observations XML: " + e.toString());
        return transformer;
    }
    var ccda = new Namespace("urn:hl7-org:v3"); // Define the CCDA namespace    
    default xml namespace = ccda; // Set default namespace so ccda tags can be used without prefix
    var matchingObservations = [];

    // Extract all observation code attributes
    try {
	    if (channelMap.get('ehrVendor') == 'Medent') {
		    matchingObservations = xml..component
		        .structuredBody
		        .component
		        .section.(@ID == 'observations')
		        .entry
		        .observation
		        .entryRelationship
		        .observation.(code.@code == '96778-6');
	     }
	     else {
	     	matchingObservations = xml..component
		        .structuredBody
		        .component
		        .section.(@ID == 'observations')
		        .entry
		        .observation
		        .entryRelationship
		        .observation
		        .entryRelationship
		        .observation.(code.@code == '96778-6');
	     }
     } catch (err) {
        logger.error("XPath error while extracting observations: " + err.toString());
        return transformer;
    }

	var seenCodes = {};
	var codingArray = [];

	for each (var obs in matchingObservations) {
	    for each (var val in obs.value) {
	        var code = val.@code.toString();
	        var display = val.@displayName.toString();

	        if (code && !seenCodes[code]) {
	            codingArray.push({
	                "system": "http://loinc.org",
	                "code": code,
	                "display": display
	            });
	            seenCodes[code] = true;
	        }
	    }
	}

	transformer.setParameter('componentAnswersXml', JSON.stringify(codingArray));
	logger.info("componentAnswersXml : " + JSON.stringify(codingArray));
	return transformer;
}

// Define Namespace Resolver
function setResourceIdParameters(transformer) {
    	// Parse XML
	var sourceXml = $('phi_filtered_ccd');
	var xmlDoc = new XML(sourceXml);
	default xml namespace = "urn:hl7-org:v3";

	// Generate Bundle resource Id
	var ccda_fhir_bundle_id = channelMap.get('interactionId');
	if (ccda_fhir_bundle_id) {
		transformer.setParameter('bundleId', ccda_fhir_bundle_id);
		logger.info('bundleId : ' + ccda_fhir_bundle_id);
	}
	else {
		transformer = generateSHA256Id(xmlDoc, "ClinicalDocument", "bundleId", transformer);
	}

	// Extract `patientRole`
	var patientRoles = xmlDoc.*::recordTarget.*::patientRole[0];
	getPatientMRN(patientRoles);
	transformer = generateSHA256Id(patientRoles, "patientRole", "patientResourceId", transformer);

	// Extract `consent`
	var consent = xmlDoc.*::authorization.*::consent[0];
	var currentTimestamp = new java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss.SS'Z'").format(new java.util.Date());
	transformer = generateSHA256Id(consent, "consent", "consentResourceId", transformer, currentTimestamp);

	// Extract `author (organization)`
	var author = xmlDoc.*::author[0];
	transformer = generateSHA256Id(author, "Author", "organizationResourceId", transformer);

	// Extract `Questionnaire`
	var Questionnaire = xmlDoc.*::component
                              .*::structuredBody
                              .*::component
                              .*::section.(@ID != 'sexualOrientation')
                              .*::entry;
	transformer = generateSHA256Id(Questionnaire, "Questionnaire", "questionnaireResourceId", transformer);

	// Extract `observations`
	try {
		var observations = xmlDoc.*::component
	                         .*::structuredBody
	                         .*::component
	                         .*::section.(@ID == 'observations')
	                         .*::entry
	                         .*::observation
	                         .*::entryRelationship
	                         .*::observation
	                         .*::entryRelationship;
		transformer = generateSHA256Id(observations, "observations", "observationResourceSha256Id", transformer);
	} catch (e) {
	    logger.error("Error accessing Observations: " + e);
	}

	// Extract `sexualOrientation`
	var sexualOrientation = xmlDoc.*::component
                              .*::structuredBody
                              .*::component
                              .*::section.(@ID == 'sexualOrientation')
                              .*::entry
                              .*::observation;
	transformer = generateSHA256Id(sexualOrientation, "sexualOrientation", "sexualOrientationResourceId", transformer);

	// Extract `encompassingEncounter`
	try {
		var encounter = xmlDoc.*::componentOf.*::encompassingEncounter;
		if (encounter != undefined && encounter.length() > 0) {
		    var encounterEffectiveTime = getEncounterEffectiveTime(encounter);	
		    transformer = generateSHA256Id(encounter, "encounter", "encounterResourceId", transformer, encounterEffectiveTime);
		} else {
		    // Try fallback to `encounter`
		    var encounters = xmlDoc.*::component
	                     .*::structuredBody
	                     .*::component
	                     .*::section.(@ID == 'encounters')
	                     .*::entry[0]
	                     .*::encounter;
		    if (encounters != undefined && encounters.length() > 0) {
		        var encounterEffectiveTime = getEncounterEffectiveTime(encounters);
		        transformer = generateSHA256Id(encounters, "encounter", "encounterResourceId", transformer, encounterEffectiveTime);
		    } else {
		        logger.error("Encounter resource not found in the XML.");
		    }
		}
	} catch (e) {
	    logger.error("Error accessing encounter: " + e);
	}

	///Grouper Observation Resource 
	var mrn = channelMap.get('Patient-MRN') || "";
     var cin = channelMap.get('patientCIN') || "";
     var currentTimestamp = new java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss.SS'Z'").format(new java.util.Date());
	var combinedText = mrn + cin + 'Grouper Observation Resource' + currentTimestamp;
     var sha256Hash = generateSHA256(new java.lang.String(combinedText)); 

     transformer.setParameter('grouperObservationResourceSha256Id', sha256Hash);
     logger.info('grouperObservationResourceSha256Id' + " : " + sha256Hash);

     transformer = getObservationCategoryCodes(transformer, sourceXml); //observations
     transformer = getMultipleAnswersForComponent(transformer, sourceXml); //observations

     //For Epic files, use the value of the header variable,'X-TechBD-Screening-Code', as the Grouper Screening Code, otherwise use the LOINC code '100698-0'
     //For Medent files, take the value from code.code
     var grouperScreeningCode = channelMap.get('X-TechBD-Screening-Code');
     if (channelMap.get('ehrVendor') == 'Epic' && (!grouperScreeningCode || grouperScreeningCode.trim() === '')) {
	    grouperScreeningCode = '100698-0';
	    logger.info("Defaulting grouperScreeningCode to 100698-0 for Epic");
	}
	if (!grouperScreeningCode) {
	    grouperScreeningCode = '';
	}
     transformer.setParameter('grouperScreeningCode', String(grouperScreeningCode));
     logger.info('grouperScreeningCode' + " : " + grouperScreeningCode);

	// Extract `location`
     try {
	    var location;
	    var location_name;
	    var location_address;

	    // Primary location path: componentOf → encompassingEncounter → location → healthCareFacility → location
	    if (
	        xmlDoc.componentOf &&
	        xmlDoc.componentOf.encompassingEncounter &&
	        xmlDoc.componentOf.encompassingEncounter.location &&
	        xmlDoc.componentOf.encompassingEncounter.location.healthCareFacility &&
	        xmlDoc.componentOf.encompassingEncounter.location.healthCareFacility.location
	    ) {
	        location = xmlDoc.componentOf.encompassingEncounter.location.healthCareFacility.location;
	        location_name = xmlDoc.componentOf.encompassingEncounter.location.healthCareFacility.location.name;
	        location_address = xmlDoc.componentOf.encompassingEncounter.location.healthCareFacility.location.addr;
	    }

	    // Fallback path: structuredBody → encounters section → encounter → participant → participantRole → playingEntity → name
	    if (!location || location.length() == 0) {
	        var section = xmlDoc.component.structuredBody.component.section.(@ID == 'encounters');
	        if (
	            section &&
	            section.entry &&
	            section.entry.length() > 0 &&
	            section.entry[0].encounter &&
	            section.entry[0].encounter.participant &&
	            section.entry[0].encounter.participant.participantRole &&
	            section.entry[0].encounter.participant.participantRole.playingEntity
	        ) {
	            location = section.entry[0].encounter.participant.participantRole;
	            location_name = section.entry[0].encounter.participant.participantRole.playingEntity.name;
	            location_address = section.entry[0].encounter.participant.participantRole.addr;
	        }
	    }

	    if (
	        location &&
	        location_name != undefined && location_name.toString().trim() !== "" &&
	        location_address != undefined && location_address.toString().trim() !== ""
	    ) {
	        transformer = generateSHA256Id(location, "Location", "locationResourceId", transformer);
	    } else {
	        logger.info("Location not found in the XML.");
	    }

	} catch (e) {
	    logger.info("Error accessing location: " + e);
	}

	return transformer;
}

/**
* Util function to get FHIR resource profile URLs and set as parameters to the transformer which converts the XML file to FHIR Bundle.
*/
function set_profile_url(transformer, profileUrl, urlEnv, metaProfileUrlName) {
	if(profileUrl != null) {
		transformer.setParameter(metaProfileUrlName, profileUrl);
		channelMap.put(metaProfileUrlName, profileUrl);
		logger.info(metaProfileUrlName + ": " + profileUrl);
	} else {
		var errorMessage = urlEnv + ' variable is not set';
		logger.error(errorMessage);
		setErrorResponse(500, errorMessage); // Set the HTTP response status to 500 (Server error)
		throw errorMessage; // Stop further processing by throwing an exception
	}
	return transformer;
}

function set_fhir_resource_profile_urls(transformer) {
	var baseFhirUrl = java.lang.System.getenv("BASE_FHIR_URL");   
	var bundleMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_BUNDLE"); 
	var patientMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_PATIENT"); 
	var encounterMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_ENCOUNTER"); 
	var consentMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_CONSENT"); 
	var organizationMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_ORGANIZATION"); 
	var observationMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_OBSERVATION"); 
	var observationSexualOrientationMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_SEXUAL_ORIENTATION"); 
	var questionnaireMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_QUESTIONNAIRE"); 
	var questionnaireResponseMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_QUESTIONNAIRE_RESPONSE"); 
	var practitionerMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_PRACTITIONER");
	var procedureMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_PROCEDURE");
//	var locationMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_LOCATION");
	var locationMetaProfileUrl = "http://hl7.org/fhir/us/sdoh-clinicalcare/StructureDefinition/SDOHCC-Location";

	transformer = set_profile_url(transformer, baseFhirUrl, 'BASE_FHIR_URL', 'baseFhirUrl');
	transformer = set_profile_url(transformer, bundleMetaProfileUrl, 'PROFILE_URL_BUNDLE', 'bundleMetaProfileUrl');
	transformer = set_profile_url(transformer, patientMetaProfileUrl, 'PROFILE_URL_PATIENT', 'patientMetaProfileUrl');
	transformer = set_profile_url(transformer, encounterMetaProfileUrl, 'PROFILE_URL_ENCOUNTER', 'encounterMetaProfileUrl');
	transformer = set_profile_url(transformer, consentMetaProfileUrl, 'PROFILE_URL_CONSENT', 'consentMetaProfileUrl');
	transformer = set_profile_url(transformer, organizationMetaProfileUrl, 'PROFILE_URL_ORGANIZATION', 'organizationMetaProfileUrl');
	transformer = set_profile_url(transformer, observationMetaProfileUrl, 'PROFILE_URL_OBSERVATION', 'observationMetaProfileUrl');
	transformer = set_profile_url(transformer, observationSexualOrientationMetaProfileUrl, 'PROFILE_URL_SEXUAL_ORIENTATION', 'observationSexualOrientationMetaProfileUrl');
	transformer = set_profile_url(transformer, questionnaireMetaProfileUrl, 'PROFILE_URL_QUESTIONNAIRE', 'questionnaireMetaProfileUrl');
	transformer = set_profile_url(transformer, questionnaireResponseMetaProfileUrl, 'PROFILE_URL_QUESTIONNAIRE_RESPONSE', 'questionnaireResponseMetaProfileUrl');
	transformer = set_profile_url(transformer, practitionerMetaProfileUrl, 'PROFILE_URL_PRACTITIONER', 'practitionerMetaProfileUrl');
	transformer = set_profile_url(transformer, procedureMetaProfileUrl, 'PROFILE_URL_PROCEDURE', 'procedureMetaProfileUrl');
	transformer = set_profile_url(transformer, locationMetaProfileUrl, 'PROFILE_URL_LOCATION', 'locationMetaProfileUrl');

	transformer.setParameter("patientCIN", channelMap.get('patientCIN')); // Pass the parameter to XSLT
	if (channelMap.get('organizationNPI')) {
		transformer.setParameter("organizationNPI", channelMap.get('organizationNPI')); // Pass the parameter to XSLT
	}		
	if (channelMap.get('organizationTIN')) {
		transformer.setParameter("organizationTIN", channelMap.get('organizationTIN')); // Pass the parameter to XSLT
	}
	if (channelMap.get('facilityID')) {
		transformer.setParameter("facilityID", channelMap.get('facilityID')); // Pass the parameter to XSLT
	}
	if (channelMap.get('encounterType')) {
		transformer.setParameter("encounterType", channelMap.get('encounterType')); // Pass the parameter to XSLT
	}

	//Generate resource ids and set as parameter to pass to xslt 
	transformer = setResourceIdParameters(transformer);

	return transformer;
}

/**
*	Function to Check the existance of Consent section in the CCDA xml file and set the header variable 'X-TechBD-Elaboration'.
*	This function is called while the /Bundle request
*/
function getConsentResourceStatus(sourceXml) {
	try {
		var consentInfo = {};
	    	consentInfo.code = 'Consent-Status';
	    	consentInfo.status = "TechBD-Generated";

		var xmlDoc = new XML(sourceXml); // Convert XML string to XML object			
		default xml namespace = "urn:hl7-org:v3"; // Default namespace for HL7 documents

		var consentCode = xmlDoc.*::authorization.*::consent.*::code.@code.toString();
		if (consentCode != undefined && consentCode.length > 0) {
		    logger.info("Consent Code: " + consentCode);
		    if (consentCode == "59284-0") {
		        consentInfo.status = "provided";
		    }
		}
		else {
			// Iterate through all <section> elements
			for each (var section in xmlDoc..section) {
			    var sectionCode = section.code.@code.toString();

			    if (sectionCode == "47519-4") {
			        // Inside the correct section
			        for each (var entry in section.entry) {
			            for each (var observation in entry.observation) {
			                for each (var entryRel in observation.entryRelationship) {
			                    for each (var nestedObs in entryRel.observation) {
			                        var obsCode = nestedObs.code.@code.toString();
			                        if (obsCode == "105511-0") {
			                            consentInfo.status = "provided";
			                            break;
			                        }
			                    }
			                }
			            }
			        }
			    }
			}
		}		
		// Convert to JSON string (optional, for sending/logging)
		var consentJsonString = JSON.stringify(consentInfo);
		logger.info("Consent Info JSON: " + consentJsonString);
		channelMap.put('elaboration', consentJsonString);
	} catch (e) {
        var errorMsg;
        if (e instanceof JavaException) {
           errorMsg = "Error parsing Consent Resource: " + e.toString();
        } else {
           errorMsg = "Unexpected error during getting Consent Resource: " + e.message;
        }
        logger.error(errorMsg);

        // Failure: Return an OperationOutcome JSON response with validation errors
        responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "invalid")));

        throw new Error(errorMsg);
     }
}

var requestedPath = sourceMap.get('contextPath');
logger.info("Request URL: " + requestedPath);

if (requestedPath == "/") {
	return;
}

//Extract the name and extension of the file received before processing.
msg = channelMap.get('fileContent');
logger.info("msg: " + msg);

// Route based on the API call
if (requestedPath == "/ccda/Bundle/$validate/" ||
	requestedPath == "/ccda/Bundle/$validate") {
    // Forward to the channel or logic for $validate
    channelMap.put("endpoint", "validate");

    try {
		// Parse the XML content into an XML object
		var ccda_msg = new XML(msg);
		var sourceXml = ccda_msg.toXMLString();    		

          if (!sourceXml || sourceXml.length === 0) {
              logger.error("No XML data received in the request.");
              responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome("No XML data received in the request.")));
              throw new Error("No XML data received in the request.");
          }

    		logger.info("Source XML Content: " + sourceXml); 

		//Replace the space in between "X-SDOH-FLO-1570000066-Patient unable to answer" with hiphen
		sourceXml = sourceXml.replace(/X-SDOH-FLO-1570000066-Patient unable to answer/g, "X-SDOH-FLO-1570000066-Patient-unable-to-answer");
		sourceXml = sourceXml.replace(/X-SDOH-FLO-1570000066-Patient declined/g, "X-SDOH-FLO-1570000066-Patient-declined");

		validateLowHighDates(sourceXml); //Check the low and high dates in address

		/*Phi filter ccda file before validation*/
		getManufacturerModelName(sourceXml); // Load the XSLT template

		// Load the XSLT template
		var xsltPath = channelMap.get('cdaPhiFilter');
		logger.info("xsltPath: " + xsltPath);
		var xsltFile = new java.io.File(xsltPath);
		var xsltStream = new java.io.FileInputStream(xsltFile);

		// Create the transformer
		var transformerFactory = javax.xml.transform.TransformerFactory.newInstance();
		var xsltSource = new javax.xml.transform.stream.StreamSource(xsltStream);
		var transformer = transformerFactory.newTransformer(xsltSource);

		// Prepare input and output streams
		var xmlInputStream = new java.io.StringReader(sourceXml);
		var xmlOutputStream = new java.io.StringWriter();
		var originalCcdFile = new javax.xml.transform.stream.StreamSource(xmlInputStream);
		var phiFilterOutput = new javax.xml.transform.stream.StreamResult(xmlOutputStream);

		// Perform the transformation
		try {
			transformer.transform(originalCcdFile, phiFilterOutput);

			// Store the transformed XML in the channel map
			var phiFilteredXml = xmlOutputStream.toString();
			channelMap.put('phi_filtered_ccd', phiFilteredXml);
			logger.info("PHI Filtered CCD: " + phiFilteredXml);
		} catch (e) {
	            var errorMsg;
	            if (e instanceof JavaException) {
	                errorMsg = "CCD XML phi filtering failed: " + e.toString();
	            } else {
	                errorMsg = "Unexpected error during phi filtering: " + e.message;
	            }

	            // Failure: Return an OperationOutcome JSON response with validation errors
	            responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "invalid")));
	            logger.error(errorMsg);
	            throw new Error(errorMsg);
	     }
		/*End of phi filter*/

        // Validate CCD
        var xsdFilePath = channelMap.get('xsdFilePath');
        logger.info("Using XSD file path: " + xsdFilePath);    

        // Load the XSD schema
        var xsdFile = new java.io.File(xsdFilePath);
        var schemaInputStream = new javax.xml.transform.stream.StreamSource(xsdFile);
        var schema = javax.xml.validation.SchemaFactory
            .newInstance(javax.xml.XMLConstants.W3C_XML_SCHEMA_NS_URI)
            .newSchema(schemaInputStream);

        // Set up the validator
        var validator = schema.newValidator();

        // Set up the input source for the XML
        var xmlSource = new javax.xml.transform.stream.StreamSource(new java.io.StringReader(phiFilteredXml));

        // Perform the validation
        try {
        	  validator.validate(xmlSource);

            // Success: Return a structured OperationOutcome JSON response
            var successResponse = {
                "OperationOutcome": {
                    "validationResults": [
                        {
                            "operationOutcome": {
                                "resourceType": "OperationOutcome",
                                "issue": [
                                    {
                                        "severity": "information",
                                        "code": "informational",
                                        "details": {
                                            "text": "CCD XML is valid according to the XSD."
                                        }
                                    }
                                ]
                            }
                        }
                    ]
                }
            };
            responseMap.put('finalResponse', JSON.stringify(successResponse));
            logger.info("CCD XML is valid according to the XSD.");
            return 'dest_bundle_validate'; // Channel Writer 
        } catch (e) {
            var errorMsg;
            if (e instanceof JavaException) {
                errorMsg = "CCD XML validation failed: " + e.toString();
            } else {
                errorMsg = "Unexpected error during validation: " + e.message;
            }

            // Failure: Return an OperationOutcome JSON response with validation errors
            responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "invalid")));

            throw new Error(errorMsg);
        }
    } catch (e) {
        var errorMsg = "Error processing XML file upload: " + e.message;

        // Failure: Return an OperationOutcome JSON response for general errors
        responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "exception")));
        logger.error(errorMsg);
        throw e;
    }
} else if (requestedPath == "/ccda/Bundle/" || requestedPath == "/ccda/Bundle") {
	try {

	    //*********** DATA LEDGER ***************
	    var randomSuffix = Math.floor(Math.random() * 10000);
	    channelMap.put('uri', sourceMap.get('uri'));
	    // Generate current timestamp in ISO format with microsecond precision
	   var currentTimestampBegin = new Date().toISOString().replace(/\.(\d{3})Z$/, function(match, millis) {
	    // Add 3 more random digits to simulate microseconds
	    var micros = millis + (Math.floor(Math.random() * 1000)).toString().padStart(3, '0');
	    return '.' + micros + 'Z';
	   });


	    var interactionId = java.util.UUID.randomUUID().toString();
	    channelMap.put("interactionId",interactionId);
	    logger.info("InteractionId: " + interactionId);
	    var payload = JSON.stringify({
	        executedAt: currentTimestampBegin,
	        actor: "TechBD",
	        action: "received", 
	        destination: "TechBD",
	        dataId: interactionId,
	        payloadType: "hrsnBundle"
	    });


		// Check if tracking is enabled
		var trackingEnabled = java.lang.System.getenv("DATA_LEDGER_TRACKING");
		if (trackingEnabled != null && trackingEnabled.toLowerCase() == "true") {
		    logger.info("data ledger api call -BEGIN received");
		    try {
		        sendDataLedgerSync(payload);
		    } catch (e) {
		        logger.error("Error occurred while sending data to Data Ledger: " + e.message);
		    }
		    logger.info("data ledger api call -END received");
		} else {
		    logger.info("DATA_LEDGER_TRACKING is not true; skipping Data Ledger sync.");
		}
		//*********** DATA LEDGER ***************

		channelMap.put("endpoint", "bundle");

		try {
			var ccda_msg = new XML(msg);
			var sourceXml = ccda_msg.toXMLString();    		

		     if (!sourceXml || sourceXml.length === 0) {
		         logger.error("No XML data received in the request.");
		         responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome("No XML data received in the request.")));
		         throw new Error("No XML data received in the request.");
		     }

			logger.info("Source XML Content: " + sourceXml); 
		} catch (e) {
	            var errorMsg;
	            if (e instanceof JavaException) {
	                errorMsg = "CCD XML cleaning failed: " + e.toString();
	            } else {
	                errorMsg = "Unexpected error during xml cleaning: " + e.message;
	            }

	            // Failure: Return an OperationOutcome JSON response with validation errors
	            responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "invalid")));
	            logger.error(errorMsg);
	            throw new Error(errorMsg);
	     }

		//Replace the space in between "X-SDOH-FLO-1570000066-Patient unable to answer" with hiphen
		sourceXml = sourceXml.replace(/X-SDOH-FLO-1570000066-Patient unable to answer/g, "X-SDOH-FLO-1570000066-Patient-unable-to-answer");
		sourceXml = sourceXml.replace(/X-SDOH-FLO-1570000066-Patient declined/g, "X-SDOH-FLO-1570000066-Patient-declined");

		validateLowHighDates(sourceXml); //Check the low and high dates in address

		/*Phi filter ccda file before validation*/
	     getManufacturerModelName(sourceXml); // Load the XSLT template
	     getConsentResourceStatus(sourceXml); // Check the existance of Consent section in the CCDA xml file and set the header variable 'X-TechBD-Elaboration'

		// Load the XSLT template
		var xsltPath = channelMap.get('cdaPhiFilter');
		var xsltFile = new java.io.File(xsltPath);
		var xsltStream = new java.io.FileInputStream(xsltFile);

		// Create the transformer
		var transformerFactory = javax.xml.transform.TransformerFactory.newInstance();
		var xsltSource = new javax.xml.transform.stream.StreamSource(xsltStream);
		var transformer = transformerFactory.newTransformer(xsltSource);

		// Prepare input and output streams
		var xmlInputStream = new java.io.StringReader(sourceXml);
		var xmlOutputStream = new java.io.StringWriter();
		var originalCcdFile = new javax.xml.transform.stream.StreamSource(xmlInputStream);
		var phiFilterOutput = new javax.xml.transform.stream.StreamResult(xmlOutputStream);

		// Perform the transformation
		try {
			transformer.transform(originalCcdFile, phiFilterOutput);

			// Store the transformed XML in the channel map
			var phiFilteredXml = xmlOutputStream.toString();
			channelMap.put('phi_filtered_ccd', phiFilteredXml);
			logger.info("PHI Filtered CCD: " + phiFilteredXml);
		} catch (e) {
	            var errorMsg;
	            if (e instanceof JavaException) {
	                errorMsg = "CCD XML phi filtering failed: " + e.toString();
	            } else {
	                errorMsg = "Unexpected error during phi filtering: " + e.message;
	            }

	            // Failure: Return an OperationOutcome JSON response with validation errors
	            responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "invalid")));
	            logger.error(errorMsg);
	            throw new Error(errorMsg);
	     }
		/*End of phi filter*/

	//**/////////CCDA Validation Begin///////////////**///
		   // Validate CCD
	        var xsdFilePath = channelMap.get('xsdFilePath');
	        logger.info("Using XSD file path: " + xsdFilePath);    

	        // Load the XSD schema
	        var xsdFile = new java.io.File(xsdFilePath);
	        var schemaInputStream = new javax.xml.transform.stream.StreamSource(xsdFile);
	        var schema = javax.xml.validation.SchemaFactory
	            .newInstance(javax.xml.XMLConstants.W3C_XML_SCHEMA_NS_URI)
	            .newSchema(schemaInputStream);

	        var validator = schema.newValidator(); // Set up the validator       
	        var xmlSource = new javax.xml.transform.stream.StreamSource(new java.io.StringReader(phiFilteredXml)); // Set up the input source for the XML

	        // Perform the validation
	        try {
	        	validator.validate(xmlSource);
	          logger.info("CCD XML is valid according to the XSD.");            
	        } catch (e) {
	            var errorMsg;
	            if (e instanceof JavaException) {
	                errorMsg = "CCD XML validation failed: " + e.toString();
	            } else {
	                errorMsg = "Unexpected error during validation: " + e.message;
	            }

	            // Failure: Return an OperationOutcome JSON response with validation errors
	            responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "invalid")));



			//*********** DATA LEDGER ***************
	          var  currentTimestamEnd = new Date().toISOString().replace(/\.(\d{3})Z$/, function(match, millis) {
			// Add 3 more random digits to simulate microseconds
			var micros = millis + (Math.floor(Math.random() * 1000)).toString().padStart(3, '0');
			return '.' + micros + 'Z';
			});
			  var payload = JSON.stringify({
		        executedAt: currentTimestamEnd,
		        actor: "TechBD",
		        action: "sent", 
		        destination: "Invalid - CCD Conversion Failed",
		        dataId: interactionId,
		        payloadType: "hrsnBundle"
		  	  });



			if (trackingEnabled != null && trackingEnabled.toLowerCase() == "true") {
			    logger.info("data ledger api call -BEGIN sent");
			   try {
		        sendDataLedgerSync(payload);
			    } catch (e) {
			        logger.error("Error occurred while sending data to Data Ledger: " + e.message);
			    }
			    logger.info("data ledger api call -END sent");
			} else {
			    logger.info("DATA_LEDGER_TRACKING is not true; skipping Data Ledger sync for sent.");
			}
			//*********** DATA LEDGER ***************

	            // Failure: Return an OperationOutcome JSON response with validation errors
	            //responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "invalid")));
	            logger.error(errorMsg);
	            throw new Error(errorMsg);
	        }
	//**/////////CCDA Validation End////////////////**///

		// Perform the transformation
		try {

			/////////  Step 2  //////////
			// Load the XSLT template
			var xsltPath = channelMap.get('cdaPhiBundle');
			var xsltFile = new java.io.File(xsltPath);
			var xsltStream = new java.io.FileInputStream(xsltFile);

			// Create the transformer
			var transformerFactory = javax.xml.transform.TransformerFactory.newInstance();
			var xsltSource = new javax.xml.transform.stream.StreamSource(xsltStream);
			var transformer = transformerFactory.newTransformer(xsltSource);

			// Set the current timestamp parameter in ISO 8601 format
			var currentTimestamp = new java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss.SS'Z'").format(new java.util.Date());
			transformer.setParameter("currentTimestamp", currentTimestamp); // Pass the parameter to XSLT
			transformer = set_fhir_resource_profile_urls(transformer); // Pass all the profile urls as parameters to XSLT

			// Retrieve the X-TechBD-Base-FHIR-URL header
			var customBaseFhirUrl = $('headers').getHeader('X-TechBD-Base-FHIR-URL');
			logger.info("X-TechBD-Base-FHIR-URL: " + customBaseFhirUrl);

			// Retrieve the VALID_URLS environment variable (comma-separated list)
			var validUrlsEnv = Packages.java.lang.System.getenv("MC_VALID_FHIR_URLS");
			logger.info("MC_VALID_FHIR_URLS from environment: " + validUrlsEnv);

			// Convert VALID_URLS to an array for validation
			var validUrls = validUrlsEnv ? validUrlsEnv.split(",").map(function(url) { return url.trim(); }) : [];

			// Check if the header value is valid
			if (customBaseFhirUrl !== null && customBaseFhirUrl.trim() != "") {
			    if (validUrls.includes(customBaseFhirUrl)) {
			        // Store valid value in channelMap
			        transformer.setParameter("baseFhirUrl", customBaseFhirUrl);
				   channelMap.put('baseFhirUrl', customBaseFhirUrl);
			    } else {
			        // Throw a Bad Request error if the URL is not in the valid list
					var errorMessage = 'The provided X-TechBD-Base-FHIR-URL is invalid. Base-FHIR-URL taken from the environment variable.';
					logger.error(errorMessage);
			    }
			}

			logger.info("Current Timestamp: " + currentTimestamp); // Log the timestamp

			// Prepare input and output streams
			var xmlInputStream = new java.io.StringReader(phiFilteredXml);
			var xmlOutputStream = new java.io.StringWriter();
			var phiFilteredCcdFile = new javax.xml.transform.stream.StreamSource(xmlInputStream);
			var bundleOutput = new javax.xml.transform.stream.StreamResult(xmlOutputStream);

			// Perform the transformation
			try {
				transformer.transform(phiFilteredCcdFile, bundleOutput);

				// Store the transformed XML in the channel map
				var ccdFhirBundle = xmlOutputStream.toString();
				channelMap.put('ccd_fhir_bundle', ccdFhirBundle);
				logger.info("CCD FHIR Bundle: " + ccdFhirBundle);

				var jsonObject = JSON.parse(ccdFhirBundle); // Parse the string into an object
				var cleanedJsonString = JSON.stringify(jsonObject, removeEmptyValues, 2);
				logger.info("Cleaned JSON : " + cleanedJsonString);
			} catch (e) {
				var errorMsg = "Error processing XML file upload: " + e.message;	
	        		// Failure: Return an OperationOutcome JSON response for general errors
			    responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "exception")));

			    logger.error("Error during CCD FHIR bundle transformation: " + e);
			    throw e;
			}


			// Convert the XML document to JSON
			const jsonResult = cleanedJsonString;
			channelMap.put('jsonResult', jsonResult);
			responseMap.put('finalResponse', JSON.stringify(jsonResult));

		    return 'dest_bundle'; // HTTP Sender

		} catch (e) {
			var errorMsg = "Error processing XML file upload: " + e.message;
	         // Failure: Return an OperationOutcome JSON response for general errors
	         responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "exception")));

		    logger.error("Error during transformation: " + e);
		    throw e;
		}
	} catch (e) {
	        var errorMsg = "Error processing XML file upload: " + e.message;

	        // Failure: Return an OperationOutcome JSON response for general errors
	        responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "exception")));
	        logger.error(errorMsg);
	        throw e;
	}
//////////////////////////////////////////////
} else {
    // Handle invalid paths  
    var errorMsg = "Error processing XML file upload: " + e.message;
    // Failure: Return an OperationOutcome JSON response for general errors
    responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "exception")));

    logger.info("Invalid endpoint: " + requestedPath);
    throw new Error("Invalid endpoint: " + requestedPath);
}

channel.postprocessingScript:
// This script executes once after a message has been processed
// Responses returned from here will be stored as "Postprocessor" in the response map

// Version: 0.4.3 - Added support for X-TechBD-Base-FHIR-URL
// Version: 0.4.15 - Added filters to source and bundle destination and changed the globalMap usage to channelMap

var endpoint = channelMap.get("endpoint");
if(endpoint == 'bundle') {
	// Get the response from the HTTP Writer destination
	var destinationName = "dest_bundle";
	var destinationResponse = responseMap.get(destinationName);

	// Check if the response exists
	if (destinationResponse) {
	    var responseStatus = destinationResponse.getStatus();  // HTTP status code
	    var responseData = destinationResponse.getMessage();   // Response message body
		responseMap.put('finalResponse', responseData);

	    // Log the response details
	    logger.info("Response from " + destinationName + ":");
	    logger.info("Status Code: " + responseStatus);
	    logger.info("Response Data: " + responseData);
	} else {
	    logger.info("No response found for destination: " + destinationName);
	}
}

return;

channel.undeployScript:
// This script executes once when the channel is undeployed
// You only have access to the globalMap and globalChannelMap here to persist data
return;
