var PGobject = Packages.org.postgresql.util.PGobject;

function toJsonb(value) {
    var jsonbObj = new PGobject();
    jsonbObj.setType("jsonb");

	if (typeof value === "object") {
        value = JSON.stringify(value);
    }

    jsonbObj.setValue(value);
    logger.info("jsonbObj: " + jsonbObj);
    return jsonbObj;
}


function saveCcdaPayload(interactionId, tenantId, requestUri, payloadJson, operation) {

	logger.info("interactionId: " + interactionId);
	logger.info("tenantId: " + tenantId);
	logger.info("requestUri: " + requestUri);
	logger.info("payloadJson: " + payloadJson);
	logger.info("operation: " + operation);

	var DriverManager = java.sql.DriverManager;
	var conn = null;
	var stmt = null;

	try {
	    logger.info("Inside DB Try");

	    // Database connection details
		var dbUrl = globalMap.get('jdbcUrl');
		var dbUser = globalMap.get('jdbcUsername');
		var dbPassword = globalMap.get('jdbcPassword');

		var ccdaAuthoringDevice = channelMap.get('ehrVendor');
	     logger.info("ccdaAuthoringDevice ================***: " + ccdaAuthoringDevice);
	     //If any error happens in between, find the ccda source type from the input file
	if (operation === "saveOgCCDAPayload" && typeof ccdaAuthoringDevice === 'undefined' || ccdaAuthoringDevice === "" || ccdaAuthoringDevice === null){
		if (/epic/i.test(payloadJson)) { channelMap.put('ehrVendor', 'Epic'); }
		if (/medent/i.test(payloadJson)) { channelMap.put('ehrVendor', 'Medent'); }
		if (/athenahealth/i.test(payloadJson)) { channelMap.put('ehrVendor', 'AthenaHealth'); }
		ccdaAuthoringDevice = channelMap.get('ehrVendor');
		logger.info("ccdaAuthoringDevice ================***: " + ccdaAuthoringDevice);
	}
	if (ccdaAuthoringDevice == 'Other') { ccdaAuthoringDevice = null; }

		//Devl
	    conn = DriverManager.getConnection(dbUrl, dbUser, dbPassword);

	    logger.info("Database connection established successfully.");

		var sql = "select techbd_udi_ingress.register_interaction_ccda_request(?::text,?::text,?::jsonb,?::text,?::text,?::text,?::jsonb,?::text,?::text,?::jsonb,?::text,?::text,?::text,?::text,?::text,?::text,?::text,?,?::text,?,?::text,?::text,?::text,?::text,?::text,?::text,?::text,?::text,?::text,?::text)";
	    stmt = conn.prepareCall(sql);

		stmt.setString(1, interactionId);
		stmt.setString(2, requestUri);		
		if(operation === "saveOgCCDAPayload"){
			stmt.setObject(3, toJsonb(JSON.stringify({
			    nature: 'Original CCDA Payload',
			    tenant_id: tenantId
			})), java.sql.Types.OTHER);
			stmt.setString(4, "NONE");	
			stmt.setString(5, "CCDA_ACCEPT");		
		} else if (operation === "saveValidationSuccess") {
			stmt.setObject(3, toJsonb(JSON.stringify({
			    nature: 'CCDA Validation Result',
			    tenant_id: tenantId
			})), java.sql.Types.OTHER);
			stmt.setString(4, "CCDA_ACCEPT");	
			stmt.setString(5, "VALIDATION_SUCCESS");
		} else if (operation === "saveConversionSuccess") {
			stmt.setObject(3, toJsonb(JSON.stringify({
			    nature: 'Converted to FHIR',
			    tenant_id: tenantId
			})), java.sql.Types.OTHER);
			stmt.setString(4, "VALIDATION_SUCCESS");	
			stmt.setString(5, "CONVERTED_TO_FHIR");
		} else {
			stmt.setObject(3, toJsonb(JSON.stringify({
			    nature: 'CCDA Validation Result',
			    tenant_id: tenantId
			})), java.sql.Types.OTHER);
			stmt.setString(4, "CCDA_ACCEPT");	
			stmt.setString(5, "VALIDATION_FAILED");
		}
		stmt.setNull(6, java.sql.Types.VARCHAR);

		if(operation === "saveOgCCDAPayload"){
			stmt.setNull(7, java.sql.Types.OTHER);
		} else {
			stmt.setObject(7, toJsonb(payloadJson), java.sql.Types.OTHER);	
		}

		if(operation == "saveValidationSuccess" || operation == "saveValidationFailed" || operation === "saveConversionSuccess"){
			stmt.setNull(8, java.sql.Types.OTHER);
		} else {
			stmt.setString(8, payloadJson);
		}			
		stmt.setNull(9, java.sql.Types.VARCHAR);
		stmt.setNull(10, java.sql.Types.OTHER);
		stmt.setNull(11, java.sql.Types.VARCHAR);
		stmt.setNull(12, java.sql.Types.VARCHAR);
		stmt.setNull(13, java.sql.Types.VARCHAR);
		stmt.setNull(14, java.sql.Types.VARCHAR);
		stmt.setNull(15, java.sql.Types.VARCHAR);
		stmt.setNull(16, java.sql.Types.VARCHAR);
		stmt.setNull(17, java.sql.Types.VARCHAR);
		stmt.setNull(18, java.sql.Types.TIMESTAMP_WITH_TIMEZONE);
		stmt.setNull(19, java.sql.Types.VARCHAR);
		stmt.setNull(20, java.sql.Types.BOOLEAN);
		stmt.setNull(21, java.sql.Types.VARCHAR);
		stmt.setNull(22, java.sql.Types.VARCHAR);
		stmt.setNull(23, java.sql.Types.VARCHAR);
		stmt.setNull(24, java.sql.Types.VARCHAR);
		stmt.setNull(25, java.sql.Types.VARCHAR);
		stmt.setNull(26, java.sql.Types.VARCHAR);
		stmt.setNull(27, java.sql.Types.VARCHAR);
		stmt.setNull(28, java.sql.Types.VARCHAR);
		stmt.setString(29, channelMap.get('filename'));
		stmt.setString(30, ccdaAuthoringDevice);

	    var result = stmt.execute();
	    logger.info("Stored procedure executed, result: " + result);

	} catch (e) {
	    logger.error("Error executing stored procedure: " + e);
	} finally {
	    if (stmt != null) stmt.close();
	    if (conn != null) conn.close();
	    logger.info("DB connection closed.");
	}
}

/**
* Replacer function to remove empty values
*/

var interactionId = java.util.UUID.randomUUID().toString();
channelMap.put('interactionId', interactionId);
var tenantId = $('headers').getHeader('X-TechBD-Tenant-ID');
var requestedPath = sourceMap.get('contextPath');

function removeEmptyValues(key, value) {
  // Remove values that are empty strings, null, undefined, or empty objects/arrays
  if (value === "" || value === null || value === undefined || 
      (typeof value === "object" && Object.keys(value).length === 0) ||
      (Array.isArray(value) && value.length === 0)) {
    return undefined; // Exclude the key from the result
  }
  return value; // Keep the value as is
}


/**
* Util function to generate json string wit hstatus and message
*/
function createJsonResponse(status, message) {
    return JSON.stringify({ status: status, message: message });
}

/**
* Util function to set error response.
*/
function setErrorResponse(statusCode, errorMessage) {
    responseMap.put('status', String(statusCode)); // Convert statusCode to string
    responseMap.put('message', errorMessage);
    responseMap.put('finalResponse', createJsonResponse(statusCode, errorMessage));
}

/*
* Util function to generate json error message
*/
function getJsonInvalidOperationOutcome(errorMsg, code) {
	return { "OperationOutcome": {
                    "validationResults": [
                        {
                            "operationOutcome": {
                                "resourceType": "OperationOutcome",
                                "interaction_id": channelMap.get('interactionId'),
                                "issue": [
                                    {
                                        "severity": "error",
                                        "code": code,
                                        "details": {
                                            "text": errorMsg
                                        }
                                    }
                                ]
                            }
                        }
                    ]
                }
            };
}

function sendDataLedgerSync(payload) {
    logger.info("sendDataLedgerSync: " + payload);

    var apiUrl = LookupHelper.get("Config", "DATA_LEDGER_API_URL", ttlHours);

// 1️⃣ Load secret name from Lookup Manager (NOT sensitive)
var dataLedgerApiKey = LookupHelper.get("Config", "TECHBD_NYEC_DATALEDGER_API_KEY", ttlHours);
//TODO remove the below line
// 2️⃣ Fetch real API key from AWS Secrets Manager
//var dataLedgerApiKey = fetchSecret(dataLedgerSecretName);

// 3️⃣ Store real key into channelMap
channelMap.put("DATA_LEDGER_API_KEY", dataLedgerApiKey);

logger.info("✔ DataLedger API Key loaded from AWS");

    logger.info("API URL: " + apiUrl);

    if (apiUrl == null) {
        throw new Error("Environment variable DATA_LEDGER_API_URL is not set.");
    }
    if (dataLedgerApiKey == null) {
        throw new Error("Environment variable 'TECHBD_NYEC_DATALEDGER_API_KEY' is not set.");
    }

    try {
        var HttpClients = org.apache.http.impl.client.HttpClients;
        var HttpPost = org.apache.http.client.methods.HttpPost;
        var StringEntity = org.apache.http.entity.StringEntity;
        var EntityUtils = org.apache.http.util.EntityUtils;

        var httpClient = HttpClients.createDefault();
        var httpPost = new HttpPost(apiUrl);

        httpPost.setHeader("Content-Type", "application/json");
        httpPost.setHeader("Accept", "application/json");

        if (dataLedgerApiKey != null) {
        	httpPost.setHeader("x-api-key", dataLedgerApiKey); // ✅ Add x-api-key header
        }

        var entity = new StringEntity(payload, "UTF-8");
        httpPost.setEntity(entity);

        var response = httpClient.execute(httpPost);
        try {
            var statusCode = response.getStatusLine().getStatusCode();
            logger.info("sendDataLedgerSync response status: " + statusCode);
            var responseBody = EntityUtils.toString(response.getEntity());

            if (statusCode >= 200 && statusCode < 300) {
                logger.info("Data Ledger API Response: " + responseBody);
                return {
                    statusCode: statusCode,
                    body: responseBody
                };
            } else {
                logger.error("Data Ledger API Error. Status: " + statusCode + ", Response: " + responseBody);
                throw new Error("Request failed with status " + statusCode);
            }
        } finally {
            EntityUtils.consumeQuietly(response.getEntity());
        }
    } catch (error) {
        logger.error("Data Ledger API Request Failed: " + error.message);
        throw error;
    }
}

// Function to validate addr dates
function validateAddrDates(addr, contextTag) {
	try {
		default xml namespace = "urn:hl7-org:v3"; // Set default namespace

		for each (var period in addr.useablePeriod) {
		  var low = period.low.@value.toString();
		  var high = period.high.@value.toString();

		  if (low && high && high !== "UNK") {
		      var lowDate = new Date(low.substring(0, 4), low.substring(4, 6) - 1, low.substring(6, 8));
		      var highDate = new Date(high.substring(0, 4), high.substring(4, 6) - 1, high.substring(6, 8));

		      if (highDate < lowDate) {
		          var errorMessage = 'Validation Error: high date (' + high + ') is earlier than low date (' + low + ') in ' + contextTag + '.useablePeriod.';
			     logger.error(errorMessage);
			     setErrorResponse(400, errorMessage); // Set the HTTP response status to 400 (Bad Request)
			     responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMessage, "invalid")));
			     //throw errorMessage; // Stop further processing by throwing an exception
			     throw new Error(errorMessage);
		      }
		  }
		}
     } catch (error) {
        logger.error("Validation Error: " + error.message);
        throw new Error("Validation Error: " + error.message);
     }
}

function validateLowHighDates(xmlData) {
	try {	
	    var xml = new XML(xmlData);
	    default xml namespace = "urn:hl7-org:v3"; // Set default namespace

	    // 1. patientRole → addr
	    for each (var recordTarget in xml.recordTarget) {
	        var patientRole = recordTarget.patientRole;
	        for each (var addr in patientRole.addr) {
	            validateAddrDates(addr, "recordTarget.patientRole");
	        }
	    }

	    // 2. author → assignedAuthor → representedOrganization → addr
	    for each (var author in xml.author) {
	        var assignedAuthor = author.assignedAuthor;
	        if (assignedAuthor && assignedAuthor.representedOrganization) {
	            for each (var addr in assignedAuthor.representedOrganization.addr) {
	                validateAddrDates(addr, "author.assignedAuthor.representedOrganization");
	            }
	        }
	    }
	} catch (error) {
        logger.error("Validation Error: " + error.message);
        throw new Error("Validation Error: " + error.message);
     }
}

function extractClinicalDocument(rawXml) {
	try {
		//var rawXml = message.toString();  // Or sourceXml.toString(), if defined
		logger.info("rawXml: " + rawXml);

		rawXml = String(rawXml).replace(/^\uFEFF/, '').trim();  // remove BOM and leading/trailing whitespace
		var extractedXml = "";
		var xmlDeclaration = '<?xml version="1.0" encoding="UTF-8"?>\n';

		var hasXmlDeclaration = rawXml.indexOf('<?xml') !== -1;
		var startTag = hasXmlDeclaration ? '<?xml' : '<ClinicalDocument';
		var start = rawXml.indexOf(startTag);
		var end = rawXml.indexOf('</ClinicalDocument>');


		if (start !== -1 && end !== -1) {
		    end += '</ClinicalDocument>'.length;
		    extractedXml = rawXml.substring(start, end);

		    // If no XML declaration, add it manually
		    if (!hasXmlDeclaration) {
		        extractedXml = xmlDeclaration + extractedXml;
		    }

		    logger.info("Final Extracted XML: " + extractedXml);
		    channelMap.put('fileContent', extractedXml);
		    msg = channelMap.get('fileContent');

		    try{
				//DB Save Original Payload
			     var result = saveCcdaPayload(
				    interactionId,
				   tenantId,
				   requestedPath,
				   msg,
				   "saveOgCCDAPayload"
				);
			} catch (e) {
				var errorMsg;
		            if (e instanceof JavaException) {
		                errorMsg = "CCDA Og Payload save: " + e.toString();
		            } else {
		                errorMsg = "Unexpected error during saving: " + e.message;
		            }
		            throw new Error("Unexpected error during saving");
			}

		} else {
			try{
				//DB Save Original Payload
			     var result = saveCcdaPayload(
				    interactionId,
				   tenantId,
				   requestedPath,
				   rawXml,
				   "saveOgCCDAPayload"
				);
			} catch (e) {
				var errorMsg;
		            if (e instanceof JavaException) {
		                errorMsg = "CCDA Og Payload save: " + e.toString();
		            } else {
		                errorMsg = "Unexpected error during saving: " + e.message;
		            }
		            throw new Error("Unexpected error during saving");
			}

		    logger.error("Could not find ClinicalDocument boundaries in XML.");
		    responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome("Could not find ClinicalDocument boundaries in XML.")));
		    var errorResponse = getJsonInvalidOperationOutcome("Could not find ClinicalDocument boundaries in XML.");
		    var operation = "saveValidationFailed";
		    saveCcdaPayload(interactionId, tenantId, requestedPath, errorResponse, operation);
		    throw new Error("Could not find ClinicalDocument boundaries in XML.");
		}
	} catch (error) {
		try{
				//DB Save Original Payload
			     var result = saveCcdaPayload(
				    interactionId,
				   tenantId,
				   requestedPath,
				   rawXml,
				   "saveOgCCDAPayload"
				);
			} catch (e) {
				var errorMsg;
		            if (e instanceof JavaException) {
		                errorMsg = "CCDA Og Payload save: " + e.toString();
		            } else {
		                errorMsg = "Unexpected error during saving: " + e.message;
		            }
		            throw new Error("Unexpected error during saving");
			}

        logger.error("Error while extracting ClinicalDocument from in XML: " + error.message);
        responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome("Error while extracting ClinicalDocument from XML: " + error.message)));
	   var errorResponse = getJsonInvalidOperationOutcome("Error while extracting ClinicalDocument from XML: " + error.message);
	   var operation = "saveValidationFailed";
	   saveCcdaPayload(interactionId, tenantId, requestedPath, errorResponse, operation);
	   throw new Error("Error while extracting ClinicalDocument from XML: " + error.message);
     }
}

