/**
* Replacer function to remove empty values
*/
function removeEmptyValues(key, value) {
  // Remove values that are empty strings, null, undefined, or empty objects/arrays
  if (value === "" || value === null || value === undefined || 
      (typeof value === "object" && Object.keys(value).length === 0) ||
      (Array.isArray(value) && value.length === 0)) {
    return undefined; // Exclude the key from the result
  }
  return value; // Keep the value as is
}


/**
* Util function to generate json string wit hstatus and message
*/
function createJsonResponse(status, message) {
    return JSON.stringify({ status: status, message: message });
}

/**
* Util function to set error response.
*/
function setErrorResponse(statusCode, errorMessage) {
    responseMap.put('status', String(statusCode)); // Convert statusCode to string
    responseMap.put('message', errorMessage);
    responseMap.put('finalResponse', createJsonResponse(statusCode, errorMessage));
}

/*
* Util function to generate json error message
*/
function getJsonInvalidOperationOutcome(errorMsg, code) {
	return { "OperationOutcome": {
                    "validationResults": [
                        {
                            "operationOutcome": {
                                "resourceType": "OperationOutcome",
                                "issue": [
                                    {
                                        "severity": "error",
                                        "code": code,
                                        "details": {
                                            "text": errorMsg
                                        }
                                    }
                                ]
                            }
                        }
                    ]
                }
            };
}

function sendDataLedgerSync(payload) {
    logger.info("sendDataLedgerSync: " + payload);

    var apiUrl = java.lang.System.getenv("DATA_LEDGER_API_URL");
    var dataLedgerApiKey = java.lang.System.getenv("TECHBD_NYEC_DATALEDGER_API_KEY");

    logger.info("API URL: " + apiUrl);

    if (apiUrl == null) {
        throw new Error("Environment variable DATA_LEDGER_API_URL is not set.");
    }
    if (dataLedgerApiKey == null) {
        throw new Error("Environment variable 'TECHBD_NYEC_DATALEDGER_API_KEY' is not set.");
    }

    try {
        var HttpClients = org.apache.http.impl.client.HttpClients;
        var HttpPost = org.apache.http.client.methods.HttpPost;
        var StringEntity = org.apache.http.entity.StringEntity;
        var EntityUtils = org.apache.http.util.EntityUtils;

        var httpClient = HttpClients.createDefault();
        var httpPost = new HttpPost(apiUrl);

        httpPost.setHeader("Content-Type", "application/json");
        httpPost.setHeader("Accept", "application/json");

        if (dataLedgerApiKey != null) {
        	httpPost.setHeader("x-api-key", dataLedgerApiKey); // âœ… Add x-api-key header
        }

        var entity = new StringEntity(payload, "UTF-8");
        httpPost.setEntity(entity);

        var response = httpClient.execute(httpPost);
        try {
            var statusCode = response.getStatusLine().getStatusCode();
            logger.info("sendDataLedgerSync response status: " + statusCode);
            var responseBody = EntityUtils.toString(response.getEntity());

            if (statusCode >= 200 && statusCode < 300) {
                logger.info("Data Ledger API Response: " + responseBody);
                return {
                    statusCode: statusCode,
                    body: responseBody
                };
            } else {
                logger.error("Data Ledger API Error. Status: " + statusCode + ", Response: " + responseBody);
                throw new Error("Request failed with status " + statusCode);
            }
        } finally {
            EntityUtils.consumeQuietly(response.getEntity());
        }
    } catch (error) {
        logger.error("Data Ledger API Request Failed: " + error.message);
        throw error;
    }
}

// Function to validate addr dates
function validateAddrDates(addr, contextTag) {
	try {
		default xml namespace = "urn:hl7-org:v3"; // Set default namespace

		for each (var period in addr.useablePeriod) {
		  var low = period.low.@value.toString();
		  var high = period.high.@value.toString();

		  if (low && high && high !== "UNK") {
		      var lowDate = new Date(low.substring(0, 4), low.substring(4, 6) - 1, low.substring(6, 8));
		      var highDate = new Date(high.substring(0, 4), high.substring(4, 6) - 1, high.substring(6, 8));

		      if (highDate < lowDate) {
		          var errorMessage = 'Validation Error: high date (' + high + ') is earlier than low date (' + low + ') in ' + contextTag + '.useablePeriod.';
			     logger.error(errorMessage);
			     setErrorResponse(400, errorMessage); // Set the HTTP response status to 400 (Bad Request)
			     responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMessage, "invalid")));
			     //throw errorMessage; // Stop further processing by throwing an exception
			     throw new Error(errorMessage);
		      }
		  }
		}
     } catch (error) {
        logger.error("Validation Error: " + error.message);
        throw new error("Validation Error: " + error.message);
     }
}

function validateLowHighDates(xmlData) {
	try {	
	    var xml = new XML(xmlData);
	    default xml namespace = "urn:hl7-org:v3"; // Set default namespace

	    // 1. patientRole â†’ addr
	    for each (var recordTarget in xml.recordTarget) {
	        var patientRole = recordTarget.patientRole;
	        for each (var addr in patientRole.addr) {
	            validateAddrDates(addr, "recordTarget.patientRole");
	        }
	    }

	    // 2. author â†’ assignedAuthor â†’ representedOrganization â†’ addr
	    for each (var author in xml.author) {
	        var assignedAuthor = author.assignedAuthor;
	        if (assignedAuthor && assignedAuthor.representedOrganization) {
	            for each (var addr in assignedAuthor.representedOrganization.addr) {
	                validateAddrDates(addr, "author.assignedAuthor.representedOrganization");
	            }
	        }
	    }
	} catch (error) {
        logger.error("Validation Error: " + error.message);
        throw new error("Validation Error: " + error.message);
     }
}

function extractClinicalDocument(rawXml) {
	try {
		//var rawXml = message.toString();  // Or sourceXml.toString(), if defined
		logger.info("rawXml: " + rawXml);
		rawXml = String(rawXml).replace(/^\uFEFF/, '').trim();  // remove BOM and leading/trailing whitespace
		var extractedXml = "";
		var xmlDeclaration = '<?xml version="1.0" encoding="UTF-8"?>\n';

		var hasXmlDeclaration = rawXml.indexOf('<?xml') !== -1;
		logger.info("hasXmlDeclaration: " +hasXmlDeclaration);
		var startTag = hasXmlDeclaration ? '<?xml' : '<ClinicalDocument';
		var start = rawXml.indexOf(startTag);
		logger.info("start rawXml: " +start);
		var end = rawXml.indexOf('</ClinicalDocument>');
		logger.info("end rawXml: " +end);

		if (start !== -1 && end !== -1) {
		    end += '</ClinicalDocument>'.length;
		    logger.info("end rawXml2: " +end);
		    extractedXml = rawXml.substring(start, end);

		    // If no XML declaration, add it manually
		    if (!hasXmlDeclaration) {
		        extractedXml = xmlDeclaration + extractedXml;
		    }

		    logger.info("Final Extracted XML: " + extractedXml);
		    channelMap.put('fileContent', extractedXml);
		} else {
		    logger.error("Could not find ClinicalDocument boundaries in XML.");
		    responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome("Could not find ClinicalDocument boundaries in XML.")));
		    throw new Error("Could not find ClinicalDocument boundaries in XML.");
		}
	} catch (error) {
        logger.error("Error while extracting ClinicalDocument from in XML: " + error.message);
        responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome("Error while extracting ClinicalDocument from in XML: " + error.message)));
	   throw new error("Error while extracting ClinicalDocument from in XML: " + error.message);
     }
}

