        } else {
            logError("***** Failed to save original CCDA payload with interactionId: " + interactionId + " *****", channelMap);
            return false;
        }
    } catch (error) {
        logError("Error saving original CCDA payload: " + error.message, channelMap);
        return false;
    }


}

