// Helper function to get invalid operation outcome
function getJsonInvalidOperationOutcome(errorMessage, code) {
    if (!code) {
        code = "exception";
    }

    return {
        "resourceType": "OperationOutcome",
        "issue": [
            {
                "severity": "error",
                "code": code,
                "details": {
                    "text": errorMessage
                }
            }
        ]
    };
}

