// Helper function to validate and get tenant information
function getTenantInfo(tenantId) {
    // If tenantId is undefined or null, try to get it from channelMap or other sources
    if (!tenantId || tenantId === 'undefined') {
        // Try to get from channelMap
        var channelTenantId = channelMap.get("tenantId");
        if (channelTenantId) {
            logInfo("#######Using tenant ID from channelMap: " + channelTenantId, channelMap);
            return channelTenantId;
        }

        // Try to get from globalMap
        var globalTenantId = globalMap.get("tenantId");
        if (globalTenantId) {
            logInfo("Using tenant ID from globalMap: " + globalTenantId, channelMap);
            return globalTenantId;
        }

        // Default fallback
        logError("Tenant ID is undefined, using default value", channelMap);
        return "unknown";
    }
    return tenantId;
}


// Function 6: Save All Error Information (Comprehensive Error Handler)
function saveAllErrorInformation(interactionId, tenantId, requestedPath, errorMessage, errorResponse, fileName) {
   // Get fileName from parameter or use default
//var actualFileName = fileName || channelMap.get("originalFileName");
     var actualFileName = channelMap.get('filename');
