        originalPayloadSaved: false,
        validationSaved: false,
        conversionSaved: false
    };

    if (typeof interactionId !== 'undefined') {
        try {


            // Validate tenant information
            var validTenantId = getTenantInfo(tenantId);

            // 1. FIRST: Save the original CCDA payload so it's available for viewing
            if (sourceXml) {
                saveResults.originalPayloadSaved = saveOriginalCcdaPayload(interactionId, validTenantId, requestedPath, sourceXml, successResponse, fileName);
                logInfo("********Original payload save result: " + saveResults.originalPayloadSaved, channelMap);
            } else {
                logError("***** No sourceXml provided - cannot save original payload *****", channelMap);
            }


            // Only save validation failure - no FHIR conversion failure since conversion was never attempted
            saveResults.validationSaved = saveValidationFailure(interactionId, validTenantId, requestedPath, errorMessage, errorResponse , fileName);

            logInfo("***** Saved validation failure only - no conversion was attempted *****", channelMap);

        } catch (dbError) {
            logError("***** Database save error for failure: " + dbError.message + " *****", channelMap);
        }
    } else {
        logError("***** Cannot save error information - interactionId is undefined *****", channelMap);
    }

    return saveResults;
}
