channel.preprocessingScript:
// Modify the message variable below to pre process data
// Set default source for this channel
//channelMap.put("source", "CCDA"); // or "CSV", "HL7", etc.

var requestParameters = channelMap.get("requestParameters");
var SourceType = Packages.org.techbd.config.SourceType;
var source = SourceType.CCDA.name(); 

////////////////////////////////////////////////////////////////
// Get remote client address (could be IPv4 or IPv6, with port)
var clientAddr = $('remoteAddress');
var clientIp = clientAddr;


logger.info("remoteAddress: " + clientAddr);

// Handle IPv6: [address]:port
if (clientAddr != null && clientAddr.startsWith("[")) {
    // Extract between [ and ]
    var start = clientAddr.indexOf("[") + 1;
    var end = clientAddr.indexOf("]");
    clientIp = clientAddr.substring(start, end);
}
// Handle IPv4: address:port
else if (clientAddr != null && clientAddr.indexOf(":") !== -1) {
    clientIp = clientAddr.split(":")[0];
}

// Store into channel map
channelMap.put('clientIpAddress', clientIp);
//////////////////////////////////////////////////////////////

requestParameters.put(Packages.org.techbd.config.Constants.SOURCE_TYPE, source.trim());

return message;

