globalMap.put("processFHIRBundle", function(tenantId, channelMap, connectorMessageOrString, responseMap) {
    var fhirService = globalMap.get("fhirService");
    var convertMapToJson = globalMap.get("convertMapToJson");

    if (!fhirService || !convertMapToJson) {
        logger.error("Missing fhirService or convertMapToJson in globalMap.");
        return;
    }

    // Try to get raw JSON string
    var jsonString;
    try {
        if (typeof connectorMessageOrString.getRawData === 'function') {
            jsonString = connectorMessageOrString.getRawData(); // live channel
        } else {
            jsonString = connectorMessageOrString; // manual call
        }
    } catch (e) {
        logger.error("Invalid connectorMessageOrString: " + e);
        throw e;
    }

    var requestParameters = channelMap.get('requestParameters');
    var headerParameters = channelMap.get('headerParameters');
    var requestDetailsMap = new java.util.HashMap();

    if (requestParameters != null) {
        requestDetailsMap.putAll(requestParameters);
    }
    if (headerParameters != null) {
        requestDetailsMap.putAll(headerParameters);
    }

    var responseParameters = new java.util.HashMap();

    var validationResults = fhirService.processBundle(
        jsonString,
        requestDetailsMap,
        responseParameters
    );

    return convertMapToJson(validationResults);
});


globalMap.put("processCSVZipFile", function(channelMap, sourceMap, connectorMessage, responseMap,processType) {
    var csvService = globalMap.get("csvService");
    var logError = globalMap.get("logError");
    var logInfo = globalMap.get("logInfo");

    try {


	/* ----------------------------------------------------------------------
	   1.  Read the raw body exactly as Mirth received it
	---------------------------------------------------------------------- */
	var bodyStr = connectorMessage.getRawData();      // java.lang.String

	/* ----------------------------------------------------------------------
	   2.  Detect newline style (\r\n or \n) and capture the FIRST line
	---------------------------------------------------------------------- */
	var nlPos = bodyStr.indexOf('\r\n');
	var newlineLen = 2;
	if (nlPos < 0) {                     // maybe just \n
	    nlPos = bodyStr.indexOf('\n');
	    newlineLen = 1;
	}
	if (nlPos < 0) {
	    throw 'Could not detect boundary line (no newline in body).';
	}
	var dashBoundary = bodyStr.substring(0, nlPos);   // e.g. "--WebKitFormBoundary7MA4YWxkTrZu0gW"
	if (!dashBoundary.startsWith('--')) {
	    throw 'Multipart boundary line does not start with "--": ' + dashBoundary;
	}
	var boundary    = dashBoundary.substring(2);
	var endBoundary = dashBoundary + '--';

	/* ----------------------------------------------------------------------
	   3.  Work with a byte‑preserving ISO‑8859‑1 string
	---------------------------------------------------------------------- */
	var bodyIso = new java.lang.String(
	        bodyStr.getBytes('ISO-8859-1'), 'ISO-8859-1');   // lossless re‑encode

	/* ----------------------------------------------------------------------
	   4.  Find the part with  name="file"
	---------------------------------------------------------------------- */
	var dispIdx = bodyIso.indexOf('Content-Disposition: form-data; name="file"');
	if (dispIdx < 0) {
	    throw 'Could not locate multipart section with name="file".';
	}
	var hdrEnd = bodyIso.indexOf('\r\n\r\n', dispIdx);
	if (hdrEnd < 0) {
	    hdrEnd = bodyIso.indexOf('\n\n', dispIdx);           // LF‑only fallback
	    if (hdrEnd < 0) throw 'Malformed multipart header (no blank line).';
	}

	/* -------- Extract filename from Content-Disposition header ---------- */
	var dispLineStart = bodyIso.lastIndexOf('Content-Disposition', hdrEnd);
	var filenameMatch = bodyIso.substring(dispLineStart, hdrEnd).match(/filename="([^"]+)"/);
	var filename = (filenameMatch && filenameMatch.length > 1) ? filenameMatch[1] : 'uploaded-file.zip';

	// Sanitize filename to avoid path traversal or bad characters
	filename = filename.trim()
	                   .replace(/^.*[\/\\]/, '')           // remove any folder path
	                   .replace(/[^\w\-.]/g, '_');          // allow only alphanum, underscore, dash, dot

	/* ----------------------------------------------------------------------
	   5.  Set dataStart *after* CRLFCRLF (or LFLF) …
	---------------------------------------------------------------------- */
	var dataStart = hdrEnd + (bodyIso.charAt(hdrEnd) == '\r' ? 4 : 2);

	/* ---------- 5a. Trim any extra leading newline ----------------------- */
	if (bodyIso.substring(dataStart, dataStart + 2) == '\r\n') {
	    dataStart += 2;                  // skip CRLF
	} else if (bodyIso.charAt(dataStart) == '\n') {
	    dataStart += 1;                  // skip single LF
	}

	/* ----------------------------------------------------------------------
	   6.  Locate the next boundary (end of file part)
	---------------------------------------------------------------------- */
	var nextBndIdx = bodyIso.indexOf('\r\n' + dashBoundary, dataStart);
	if (nextBndIdx < 0) {                                    // maybe LF‑only
	    nextBndIdx = bodyIso.indexOf('\n' + dashBoundary, dataStart);
	}
	if (nextBndIdx < 0) {
	    throw 'Closing boundary after file part not found.';
	}
	var dataEnd = nextBndIdx;

	/* ---------- 6a. Trim trailing newline just before boundary ----------- */
	if (bodyIso.substring(dataEnd - 2, dataEnd) == '\r\n') {
	    dataEnd -= 2;
	} else if (bodyIso.substring(dataEnd - 1, dataEnd) == '\n') {
	    dataEnd -= 1;
	}

	/* ----------------------------------------------------------------------
	   7.  Extract pure ZIP bytes
	---------------------------------------------------------------------- */
	var attachmentBytes = bodyIso.substring(dataStart, dataEnd)
	                             .getBytes('ISO-8859-1');
	logger.info('Recovered ZIP byte length: ' + attachmentBytes.length);

	/* Optional sanity check: first four bytes should be 80 75 3 4 (PK\03\04) */
	logger.info(
	    'First four ZIP bytes: ' +
	    (attachmentBytes[0] & 0xff) + ' ' +
	    (attachmentBytes[1] & 0xff) + ' ' +
	    (attachmentBytes[2] & 0xff) + ' ' +
	    (attachmentBytes[3] & 0xff));

	/* ----------------------------------------------------------------------
	   8.  Wrap and validate
	---------------------------------------------------------------------- */
	var SimpleMultipartFile = Packages.org.techbd.service.csv.SimpleMultipartFile;  //Packages.org.springframework.web.multipart.MultipartFile
	var multipartFile = new SimpleMultipartFile(
	        filename,
	        'application/zip',
	        attachmentBytes);

	var requestParameters  = channelMap.get('requestParameters');
	var headerParameters   = channelMap.get('headerParameters');
	var requestDetailsMap  = new java.util.HashMap(); // Map<String,Object>

	var Constants = Packages.org.techbd.Constants;


	if (requestParameters != null) {
	    requestDetailsMap.putAll(requestParameters);
	}
	if (headerParameters != null) {
	    requestDetailsMap.putAll(headerParameters);
	}
	var responseParameters = new java.util.HashMap();
	if(processType == "validate"){
	var result = csvService.validateCsvFile(
	        multipartFile,
	        requestDetailsMap,
	        /*headerParameters,*/
	        responseParameters);

	       /* Packages.org.techbd.util.CoreFHIRUtil.addCookieAndHeadersToResponse(
		    response,
		    responseParameters,
		    requestDetailsMap
			);
			*/

	        return convertMapToJson(result);
	}else if(processType == "bundle"){
		var result = csvService.processZipFile(
	        multipartFile,
	        requestDetailsMap,
	        /*headerParameters,*/
	        responseParameters);

	        /*
	        Packages.org.techbd.util.CoreFHIRUtil.addCookieAndHeadersToResponse(
		    response,
		    responseParameters,
		    requestDetailsMap
			);
			*/

	        return convertMapToJson(result);

	}

	return null; 

    } catch (e) {
        logError("CSV Validation Error: " + e, channelMap);
        return null;
    }
});


