 else {

var logInfo = globalMap.get("logInfo");
var interactionId = java.util.UUID.randomUUID().toString();
var sdf = new java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss'Z'");
channelMap.put("interactionId", interactionId);
